import './AddPost.css';
import { Link } from "react-router-dom";
import React from 'react';
import { addNews } from './actions';

class AddPost extends React.Component
{
    constructor(props) {
        super(props);

        this.state = {
          text: '',
          title: '',
          date: '',
          author: '',
        };

        this.onFormSubmit = this.onFormSubmit.bind(this);
        this.onTaskNameChange = this.onTaskNameChange.bind(this);
        this.onPromptChange = this.onPromptChange.bind(this);
        this.backToList = this.backToList.bind(this);
      }
    onTaskNameChange(e) {
        this.setState({
          task_name: e.target.value
        });
      }

      onPromptChange(e) {
        this.setState({
          prompt: e.target.value
        });
      }

      backToList() {
        this.props.history('/');
      }

      onFormSubmit(e) {
        e.preventDefault();

        let dispatch = this.props.dispatch;

        fetch('/add_post', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            task_name: this.state.task_name,
            prompt: this.state.prompt
          })
        })
        .then(res => res.json())
        .then(data => {

          dispatch(addNews(data.text, data.title, data.authord, data.date));
          this.backToList();
        })
      }

  render()
  {
    console.log(this.props.news);

    return (

        <form onSubmit={this.onFormSubmit}>
            <h3>Добавление нового поста</h3>
            <div className="w3-panel w3-red">
                Failed
            </div>
            <div className="w3-panel w3-green">
                Good
            </div>
            <fieldset>
                <legend><span className="section">1</span>Основная информация поста</legend>
                <label htmlFor="name">Заголовок:</label>
                <input 
                    type="text" 
                    name="title" 
                    maxLength="100" 
                    title="Имя" 
                    value={this.state.prompt}
                    onChange={this.onTitleChange}
                    required />
                <label htmlFor="name">Описание:</label>
                <textarea name="text" placeholder="Описание поста"></textarea>
                <label htmlFor="password" required>Фотография:</label>
                <input 
                    type="file" 
                    multiple
                    accept="image/*"
                    className="custom-file-input" 
                    name="image" 
                    id="image"
                    value={this.state.prompt}
                    onChange={this.onImageChange}> </input>
            </fieldset>
            <button type="submit">Создать</button>
        </form>
    );
  }
}

export default AddPost;
