import { combineReducers } from 'redux'
import {
  ADD_NEWS,
  ADD_NEWS_ALL,
  TOGGLE_NEWS
} from './actions'

function news(state = [], action) {
  switch (action.type) {
    case ADD_NEWS:
      const news = [
        ...state,
        {
          _id: action.id,
          text: action.text,
          title: action.title,
          author: action.author,
          date: action.date
        }
      ]
      //debugger;
      return news
    case ADD_NEWS_ALL:
      return [...action.news]
    case TOGGLE_NEWS:
      return state.map((news) => {
        if (todo._id === action.id) {
          return Object.assign({}, todo, {
            done: !todo.done
          })
        }
        return todo
      })
    default:
      return state
  }
}

const todoApp = combineReducers({
  todos
})

export default newsApp
