/*
 * типы экшенов
 */

export const ADD_NEWS = 'ADD_NEWS'
export const ADD_NEWS_ALL = 'ADD_NEWS_ALL'
export const TOGGLE_NEWS = 'TOGGLE_NEWS'


/*
 * генераторы экшенов
 */

export function addNews(text, prompt, id) {
  return { type: ADD_NEWS, text, prompt, id }
}

export function addNewsAll(todos) {
  return { type: ADD_NEWS_ALL, todos }
}

export function toggleNews(id) {
  return { type: TOGGLE_NEWS, id }
}
